mapper.update:
update cart
set su=su+#{su}
where userid=#{userid} and pcode=#{pcode}

mapper.select:
select count(*)
from cart
where userid=#{userid}

mapper.update:
update member
set phone=#{mPhone}
where userid=#{userid}

update baesong
set name=#{name}, zip=#{zip}, juso=#{juso}
,jusoEtc=#{jusoEtc}, phone=#{phone},req=#{req},gibon=#{gibon}
where id=#{id}
